    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            String productName = jTextField2.getText();
            double price = Double.parseDouble(jTextField3.getText());
            String productId = jTextField1.getText();
            Product product = new Product(productName, price, productId);
            system.regProd(product);
            if (system.getProductList().contains(product)) {
                JOptionPane.showMessageDialog(this.rootPane, "Product created successfully");
                setVisible(false);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Error");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

